INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (913, 'https://www.amazon.ca/French-Prepositions-Trudie-Maria-Booth/dp/0761826114?ref=pf_vv_at_pdctrvw_dp', NULL, 'French Prepositions is an indepth study of the forms of both simple and compound prepositions and their use in modern French. It is designed for use by teachers and students who wish to express themselves correctly in French and are looking for a complete and reliable reference book.', 111.27, NULL);
INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (912, 'https://www.amazon.ca/Ember-Falls-Green-Book/dp/0996436804?ref=pf_vv_at_pdctrvw_dp', 4.8, 'The stage is set. It''s war. Morbin Blackhawk, slaver and tyrant, threatens to destroy the rabbit resistance forever. Heather and Picket are two young rabbits improbably thrust into pivotal roles. The fragile alliance forged around the young heir seems certain to fail. Can Heather and Picket help rescue the cause from a certain, sudden defeat? My Place Beside You My Blood For Yours Till The Green... Ember Falls', NULL, 8);
