INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (947, 'https://www.amazon.ca/Accents-Manual-Actors-Revised-Expanded/dp/087910967X?ref=pf_vv_at_pdctrvw_dp', 3.2, 'Note: This ASIN doesn''t include a CD. (Limelight). This practical reference manual, with its precise, authentic instructions on how to speak in more than 100 dialects, has established itself as the most useful and comprehensive guide to accents available, now increased by a third in this revised printing. As before, the accents range from regional U.S. and British dialects to European accents that include, among others, the Germanic, Slavic and Romance Languages. Completing his around-the-world journey, the author then covers the Middle East, Africa and Asia. Online audio is accessed at halleonard.com/mylibrary', 24.4, 9);
