INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (1190, 'https://www.amazon.ca/Tales-Seaside-Claire-Buss/dp/1717809251?ref=pf_vv_at_pdctrvw_dp', NULL, 'Tales from the Seaside is a humorous collection of short stories reflecting on life by the seaside, attempts to successfully wrangle two small children and the result of being inspired by the sun, the sand and the sea. The perfect beach read, it will have you chuckling in your deckchair.', 8.52, NULL);
INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (1185, 'https://www.amazon.ca/Functional-Equations-MathsWorks-Teachers-Leigh-Lancaster/dp/0864314922?ref=pf_vv_at_pdctrvw_dp', NULL, 'Functional equations provides mathematics teachers with an introduction to elementary aspects of functional equations. These equations are linked to function in various topics of the senior secondary mathematics curriculum including transformations, identities difference equations and mathematical modelling.', NULL, NULL);
INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (1214, 'https://www.amazon.ca/Roger-Zelaznys-Chaos-Amber-Dawn/dp/0743474945?ref=pf_vv_at_pdctrvw_dp', 2.9, 'In CHAOS AND AMBER, Dworkin and his son Oberon arrive at the Courts of Chaos to discover, and confront, their hidden enemies. But things don''t go as planned. Oberon has a terrible physical reaction to being in Chaos, while assassination attempts are made on both his and Dworkin''s lives-and the traitor in their family remains a hidden but quite real threat. Dworkin takes Oberon on a desperate journey, pressing deeper into Shadow than ever before. Here, Oberon discovers more of the true nature of his father...and of his real mother. But they have been followed, and a horde of hell-creatures attacks. Ultimately, Dworkin must create a new Pattern with his own blood to save himself, his family, and the future.', 48.71, 11);
