INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (433, 'https://www.amazon.ca/Red-Prophet-Orson-Scott-Card/dp/0312930437/ref=sr_1_1_twi_har_2?s=books&ie=UTF8&qid=1550378674&sr=1-1&keywords=Alvin+Maker+2+-+Red+Prophet+by+Orson+Scott+card', 4.4, ' Alvin Maker is drawn into the political maneuverings of his visionary brother and a French colonel exiled to Fort Detroit named Napoleon Bonapart, in this tale set in the frontier of a magical alternate America', 12.77, 13);
INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (434, 'https://www.amazon.ca/Prentice-Alvin-Orson-Scott-Card/dp/0312931417/ref=sr_1_1_twi_har_2?s=books&ie=UTF8&qid=1550378683&sr=1-1&keywords=Alvin+Maker+3+-+Prentice+Alvin+by+Orson+Scott+card', 3.4, ' Alvin returns to his birthplace to become the blacksmith''s apprentice and also to learn--hopefully with the help of the Torch-girl--to be a Maker', 9.42, 11);
INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (401, 'https://www.amazon.ca/Draco-Ian-Watson/dp/0743443187/ref=sr_1_1_twi_mas_2?s=books&ie=UTF8&qid=1550431012&sr=1-1&keywords=The+Inquisition+War+by+Ian+Watson', 3.8, ' While working to suppress a rebellion, Inquisitor Jac Draco discovers not only a bizarre entity but also a deadly secret conspiracy of ruthless inquitors who are out to seize control of every human being on the planet. Original.', NULL, 6);
