INSERT INTO public."Reviews" (review_id, user_id, book_id, rating, review) VALUES (471, 561, 87, 5, 'I''d say that if one attempt to look for a book that revealed the sexiest parts of (pure) mathematics, this is definitely have to be the one! - My best gift ever.');
