INSERT INTO public."Reviews" (review_id, user_id, book_id, rating, review) VALUES (1459, 1315, 485, 5, 'This book was intense and kept me reading for more of the exciting and nerve wracking action. I have read two of this series and truly enjoy them. Well done!');
