INSERT INTO public."Reviews" (review_id, user_id, book_id, rating, review) VALUES (2370, 1620, 832, 5, 'A fantasy novel that is only lightly touched with obvious magic.  Redwall maintains its charm in telling a sort of medieval fairy tale...but with animals as your main cast.');
