INSERT INTO public."AmazonDetails" (book_id, book_link, rating, synopsis, price, num_reviews) VALUES (78, 'https://www.amazon.ca/Understanding-Probability-Chance-Rules-Everyday/dp/0521833299/', 4.0, ' Mastering the concepts of probability can cast new light on situations in which randomness and chance appear to rule. With an emphasis on why probability works and how it can be applied, Henk Tijms introduces the reader to the world of probability in an informal way. Lotteries and casino games provide a natural source of motivation, and he carefully discusses these with many worked examples to illustrate the key concepts from probability theory. Henk Tijms has been a professor in Operations Research at the Vrije University in Amsterdam since 1976. He is the author of several textbooks, including A First Course in Stochastic Models (Wiley, 2003), as well as numerous papers on applied probability and stochastic optimization in international journals including Applied Probability and Probability in the Engineering and Informational Sciences and others.', NULL, NULL);
